def longest_common_subsequence(s1, s2):
    m = len(s1)
    n = len(s2)
    lcs = [[0 for x in range(n)] for x in range(m)]
    for i in range(m):
        for j in range(n):
            if i > j:
                lcs[i][j] = lcs[j][i] = 1
            if s1[i] == s2[j]:
                lcs[i][j] = lcs[j][i] = 1
            elif lcs[i][i] == 1:
                lcs[i][j] = 1
            else:
                lcs[i][j] = 0
    return lcs[-1][(-1)]

