def Fibonacci(n):
    if n <= 1:
        return n
    else:
        return (1 + Fibonacci(n-1)) + Fibonacci(n-2)

def binary_search(arr, target):
    start = 0
    end = len(arr) - 1
    while start <= end:
        mid = (start + end) / 2
        if arr[mid] == target:
            return mid
        elif arr[mid] < target:
            start = mid + 1
        else:
            end = mid - 1
    return -1

class Queue:
    def __init__(self):
        self.stack1 = []
        self.stack2 = []

    def add_element(self, value):
        if value in self.stack1:
            self.stack2.append(value)
        else:
            self.stack1.append(value)
            self.stack2 = [None] * 2

    def get_element(self, value):
        if value in self.stack1 and self.stack2:
            return self.stack1[-1].value + self.stack2[-1].value
        else:
