  for (var i = 0; i < arr.length; i++) {
    if (arr[i] && arr[i].length > 5 && arr[i].includes(Digit)) {
      filteredArr.push(arr[i]);
    }
  }
  if (filteredArr.length > 0) {
    filteredArr = filteredArr.filter(str => str!== '');
    for (var i = 0; i < filteredArr.length; i++) {
      filteredArr[i] = str = filteredArr[i];
      while (str.length <= 5 && i < filteredArr.length) {
        str = str + str.substr(0, 5) + str.substr(5);
        i++;
      }
      if (!str) {
        break;
      }
      filteredArr[i] = str;
    }
  }
  return filteredArr;
}

