public class A {

    public static void main(String[] args) {
        Scanner in = new Scanner(System.in);
        int t = in.nextInt();
        for (int i = 0; i < t; i++) {
            int n = in.nextInt();
            ArrayList<Integer> list = new ArrayList<>();
            for (int j = 0; j < n; j++) {
                list.add(in.nextInt());
            }
            Collections.sort(list);
            int sum = 0;
            for (int j = 0; j < n; j++) {
                sum += list.get(j);
            }
