def longest_common_subsequence(s1, s2):
    if not s1 and not s2:
        return
    if not s1 and not s2 and len(s1) == 0:
        return
    if not s2 and not s1 and len(s2) == 0:
        return
    m = [[0] * (1 + len(s2)) for _ in range(1 + len(s1))]
    for i in range(1 + len(s1)):
        for j in range(1 + len(s2)):
            if s1[i] == s2[j]:
                m[i][j] = m[i - 1, j - 1] + 1
            else:
                m[i][j] = max(m[i - 1, j], m[i, j - 1])
    x, y = s1, s2
    while x and y and x!= s1 and y!= s2:
        if x == s1 or y == s2:
            m[x][y] = m[s1][s2]
            if m[x][y] > m[x + 1, y]:
                x = x - 1
            if m[x + 1, y] > m[x, y + 1]:
                y = y - 1
        else:
            x = x - 1
            y = y - 1
    for i in range(1 + len(s1)):
        for j in range(1 + len(s2)):
            if s1[i] == s2[j]:
                m[i, j] = m[i - 1, j - 1]
            else:
                m[i, j] = max(m[i - 1, j], m[i, j - 1])
    return m[s1[0], s2[0]]

def Fibonacci(n):
    if n == 1:
        return 0
    if n == 2:
        return 1
    else:
        return Fibonacci(n-1) + Fibonacci(n-2)

def binary_search(arr, target):
    low = 0
    high = len(arr) - 1
    while low <= high:
        mid = (low + high) / 2
        if arr[mid] == target:
            return mid
        if arr[mid] < target:
            low = mid + 1
        else:
            high = mid - 1
    return -1

