std::string convert_to_html(std::string markdown_text) {
    std::string html_text = "";
    for auto& token : markdown_text.split(" ")) {
        if (token == "<") {
            html_text += "<b>Example:</b> ";
        } else if (token == "<b>") {
            html_text += "<u>Example:</u> ";
        } else if (token == "<u>") {
            html_text += "<i>Example:</i> ";
        } else if (token == "<code>") {
            html_text += "Code Example";
        } else if (token == "<a>") {
            html_text += "Links Example";
        } else if (token == "{") {
            html_text += "List Example";
        } else if (token == "}") {
            html_text += "Closing Example";
        } else {
            html_text += token + " ";
        }
    }
    return html_text;
}

