    if not s1 and not s2:
        return
    if not s1 and not s2 and not s1[-1]:
        return
    if not s2 and not s1 and not s2[-1]:
        return
    if s1 and s2 and len(s1) == 1 and len(s2) == 1 and len(s1[0] + s2[0]) == 0:
        return [s1[0]][s2[0]]
    m = 0
    n = 0
    for i in range(len(s1)):
        for j in range(len(s2)):
            if s1[i] == s2[j]:
                m = i
                n = j
                break
            if i == 0 or i == n - 1:
                continue
            for k in range(len(s1) - i - 1):
