function capitalizeWordsAndRemoveWhitespace(input) {
  // Remove whitespace characters from the start and end of each word
  const words = input.split(" ");
  const result = words.map(word => word.trim().toUpperCase());

  // Reverse the order of the words
  const reversedWords = result.reverse();

  // Replace vowels with corresponding letters
  const vowels = ['a', 'e', 'i', 'o', 'u'];
  for (let i = 0; i < vowels.length; i++) {
    const word = reversedWords[i];
    const replacedWord = word.replace(/[aeiou]/gi, (char) => vowels[Math.floor(Math.random() * vowels.length)]);
    reversedWords[i] = replacedWord;
  }

  // Remove special characters except for whitespace
  const specialChars = ["@", "#", "$", "%", "^", "&", "*", "(", ")", "-", "+", "=", "[", "]", "{", "}", "|", "~", ">"];

  // Join the words back into a string
  const resultString = specialChars.join(" ") + reversedWords.join(" ");

  return resultString;
}

