    item_lengths = {}
    for item in item_list:
        item_lengths[item] = item.lower().split(" ")[0]
    # Sort the list based on the length of each item
    sorted_list = sorted(item_lengths.items(), key=lambda x: x[1], reverse=True)
    # Sort the list based on the alphabetical order of each item
    sorted_list = sorted(sorted_list, key=lambda x: x[0], reverse=True)
    return sorted_list
