def convert_string_to_list(string):
    # Remove special characters and numbers from the string
    string = ''.join(char for char in string if char.isalpha())

    # Create a list to store the characters in sorted order
    characters = []
    for char in string:
        # Check if the character is a letter
        if char.isalpha():
            # Convert the character to lowercase and sort it alphabetically
            characters.append(char.lower())
        else:
            # Ignore the character and move on to the next character
            continue

    # Sort the characters in descending order based on their ASCII values
    characters.sort(reverse=True)

    return characters
